import React,{useState} from 'react'
import './header.scss'

function Header () {
    //scroll내렸을때 header아랫부분 그림자 효과
    window.addEventListener("scroll", function(){
        const header = document.querySelector(".header")
        if(this.scrollY >= 80)header.classList.add("header-scroll");
        else header.classList.remove("header-scroll")
    })

    // toggle
    const [Toggle, showMenu] = useState(false)
    const [activeNav, setActiveNav] = useState("#home")

    return (
        <header className='header'>
            <nav className='nav container'>
                <a href='index.html' className='nav_logo'>Eunmi</a>

                <div className={Toggle ? 'nav_menu show-menu' : 'nav_menu'}>
                    <ul className='nav_list grid'>
                        <li className='nav_item'>
                            <a href='#home' 
                                onClick={()=> setActiveNav('#home')} 
                                className={ 
                                    activeNav === "#home" ? "nav_link active-link" :
                                    "nav_link"
                                }
                            >
                                <i className='uil uil-estate nav_icon'></i> Home
                            </a>
                        </li>

                        <li className='nav_item'>
                            <a href='#about'
                                onClick={()=> setActiveNav('#about')} 
                                className={ 
                                    activeNav === "#about" ? "nav_link active-link" :
                                    "nav_link"
                                }
                            >
                                <i className='uil uil-user nav_icon'></i> About
                            </a>
                        </li>

                        <li className='nav_item'>
                            <a href='#skills'
                                onClick={()=> setActiveNav('#skills')} 
                                className={ 
                                    activeNav === "#skills" ? "nav_link active-link" :
                                    "nav_link"
                                }
                            >
                                <i className='uil uil-file-alt nav_icon'></i> Skills
                            </a>
                        </li>

                        <li className='nav_item'>
                            <a href='' className='nav_link'
                                                            onClick={()=> setActiveNav('#skills')} 
                                                            className={ 
                                                                activeNav === "#skills" ? "nav_link active-link" :
                                                                "nav_link"
                                                            }
                            >
                                <i className='uil uil-file-alt nav_icon'></i> PROJECT
                            </a>
                        </li>

                        <li className='nav_item'>
                            <a href='' className='nav_link'>
                                <i className='uil uil-file-alt nav_icon'></i> test
                            </a>
                        </li>

                        <li className='nav_item'>
                            <a href='' className='nav_link'>
                                <i className='uil uil-file-alt nav_icon'></i> test
                            </a>
                        </li>
                    </ul>

                    <i class="uil uil-times nav_close" onClick={()=> showMenu(!Toggle)}></i>
                </div>
                <div className='nav_toggle' onClick={()=> showMenu(!Toggle)}>
                    <i class="uil uil-apps"></i>
                </div>
            </nav>

        </header>
    )
}

export default Header
