  observer.observe(div[3])
  observer.observe(div[4])
  observer.observe(div[5])
  observer.observe(div[6])
  observer2.observe(div[0]);

  return (
    <>
      <Header />

      <main className='main'>
        <Home />
        <About />
        <Skills />
        <ProjectList />
        <Kakao />
        <Movie />
        <Pure />
        <Contact />
      </main>
      <Footer />
      <ScrollUp />
    </>
  );
}

export default App;
