import React from 'react';
import './App.css';

import Header from './components/header/Header';
import Home from './components/home/Home';
import About from './components/about/About';
import Skills from './components/skills/Skills';
import ProjectList from './components/projectList/ProjectList'
import Contact from './components/contact/Contact'
import Footer from './components/footer/Footer'
import ScrollUp from './components/scrollup/ScrollUp'
import Kakao from './components/reactproject/Kakao';
import Movie from './components/reactproject/Movie';
import Pure from './components/pure/Pure';

function App() {

  //감시하는 화면
let observer = new IntersectionObserver((e)=>{
  e.forEach((on)=>{
      if(on.isIntersecting){
          on.target.style.opacity = 1;
      }else{
          on.target.style.opacity = 0;
      }
  })
})
// 세번째 스킬 관련
let observer2 = new IntersectionObserver((e)=>{
  let dot = document.getElementsByClassName('dot')
  if (e[0].isIntersecting){
      for(let i = 0; i< dot.length; i++){
          dot[i].classList.add('on')
      }
  }
  else{
      for(let i = 0; i< dot.length; i++){
          dot[i].classList.remove('on')
      }
  }
})

let div = document.querySelectorAll('section')
observer.observe(div[0]) //observe 감시해줌
observer.observe(div[2])
observer.observe(div[1])
observer.observe(div[3])
observer.observe(div[4])
observer.observe(div[5])
observer.observe(div[6])
observer2.observe(div[0]);

  return (
    <>
      <Header />

      <main className='main'>
        <Home />
        <About />
        <Skills />
        <ProjectList />
        <Kakao />
        <Movie />
        <Pure />
        <Contact />
      </main>
      <Footer />
      <ScrollUp />
    </>
  );
}

export default App;
